gradFunctionReg <- function(theta,X,y,lambda){    #initialize some useful values  m=nrow(X)  #value to return  grad <- vector(length=length(theta))    #cost function  predictions=sigmoid(X %*% theta)  theta_reg=theta[2:length(theta)]    grad=(1/m)*t(X)%*%(predictions-y)+(lambda*theta_reg)/m   # grad[1]=(1/m)*sum((predictions-y)*X[,1])   # for(j in 2:ncol(X)){  #  grad[j] <- (1/m)*sum((predictions-y)*X[,j])+lambda*theta[j]/m # }    return(grad)}