predictOneVsAll <- function(all_theta,X){    #load functions  source("./functions/sigmoid.R")    #put data into matrix  X <- as.matrix(X)    #set some useful values  m=nrow(X)    #variable to return  p <- matrix(0,m,1)    #add intercept term  X <- cbind(matrix(1,m),X)    #predict  predictions <- sigmoid(X%*%t(all_theta))  p <- apply(predictions,1,which.max)    #return value  return(p)}