learningCurveNN <- function(X,y,Xval,yval,lambda,                                  input_layer_size,                                  hidden_layer_size,                                  num_labels){    source("nnCostFunction.R")  source("trainNNReg.R")  m=nrow(X)  error_train <- matrix(0,m-1,1)  error_val <- matrix(0,m-1,1)    for(i in 2:m){        nn_params_train <- trainNNReg(X[1:i,],y[1:i,],lambda,                                  input_layer_size,                                  hidden_layer_size,                                  num_labels)    error_train[i-1] <- nnCostFunction(nn_params_train,input_layer_size,                                       hidden_layer_size,num_labels,                                       X[1:i,],y[1:i],0)    error_val[i-1] <- nnCostFunction(nn_params_train,input_layer_size,                                     hidden_layer_size,num_labels,                                     Xval,yval,0)  }    learningCurve <- list("error_train"=error_train,"error_val"=error_val)    return(learningCurve)}