trainNNReg <- function(X,y,lambda,                             input_layer_size,                             hidden_layer_size,                             num_labels){    source("nnCostFunction.R")  source("nnGradFunction.R")  source("randInitializeWeights.R")  #====initialize parameters====  #Initializing parameters  initial_Theta1 <-randInitializeWeights(input_layer_size,hidden_layer_size)  initial_Theta2 <-randInitializeWeights(hidden_layer_size,num_labels)  #unroll parameters  initial_nn_params <- matrix(c(initial_Theta1,initial_Theta2),ncol=1)    #====Training NN====  cat("Training neural networks...\n")  lambda=lambda  result <- optim(par=initial_nn_params,fn=nnCostFunction,gr=nnGradFunction,                  input_layer_size=input_layer_size,                  hidden_layer_size=hidden_layer_size,                  num_labels=num_labels,                  X=X,y=y,lambda=lambda,                  method="BFGS",                  control=list(maxit=100))  nn_params <- result$par    return(nn_params)  }