validationCurveNN <- function(X,y,Xval,yval,                                    input_layer_size,                                    hidden_layer_size,                                    num_labels){    source("trainNNReg.R")  source("nnCostFunction.R")  lambda_vec=c(0.001,0.003,0.01,0.03,0.1,0.3,1,3,10)    error_train <- matrix(0,length(lambda_vec),1)  error_val <- matrix(0,length(lambda_vec),1)    for(i in 1:length(lambda_vec)){    lambda=lambda_vec[i]    nn_params <- trainNNReg(X,y,lambda,input_layer_size,hidden_layer_size,num_labels)    error_train[i] <- nnCostFunction(nn_params,input_layer_size,hidden_layer_size,                                     num_labels,X,y,0)    error_val[i] <- nnCostFunction(nn_params,input_layer_size,hidden_layer_size,                                   num_labels,Xval,yval,0)  }    validationCurveNN <- list("error_train"=error_train,"error_val"=error_val,"lambda_vec"=lambda_vec)  return(validationCurveNN)  }