featureNormalize <- function(X){    #test  #X=trainSet    n=ncol(X)  X_norm=matrix(0,nrow(X),ncol(X))  mu=sapply(X,mean,na.rm=TRUE)  sigma=sapply(X,sd,na.rm=TRUE)    for(i in 1:n){    if(sigma[i]!=0){      X_norm[,i] <- (X[,i]-mu[i])/sigma[i]    }    else{      X_norm[,i] <- (X[,i]-mu[i])    }  }    featureNormalize <- list("mu"=mu,"sigma"=sigma,"norm"=X_norm)    return(featureNormalize)  }