learningCurve <- function(X,y,Xval,yval,lambda){    m=nrow(X)  error_train <- matrix(0,m-1,1)  error_val <- matrix(0,m-1,1)    for(i in 2:m){        #test    #i=2    theta_train <- trainLinearReg(X[1:i,],y[1:i,],lambda)$theta    error_train[i-1] <- linearRegCostFunction(theta_train,X[1:i,],y[1:i],0)    error_val[i-1] <- linearRegCostFunction(theta_train,Xval,yval,0)  }    learningCurve <- list("error_train"=error_train,"error_val"=error_val)    return(learningCurve)}