trainLogisticReg <- function(X,y,lambda){    source("./functions/costFunctionReg.R")  source("./functions/gradFunctionReg.R")  #====learn theta====  initial_theta <- matrix(0,ncol(X),1)  result <- optim(par=initial_theta,fn=costFunctionReg,gr=gradFunctionReg,X=X,y=y,lambda=lambda,                  method="BFGS")  theta <- result$par  cost <- result$value    trainLogisticReg <- list("theta"=theta,"cost"=cost)    return(trainLogisticReg)  }