validationCurve <- function(X,y,Xval,yval){    source("./functions/trainLinearReg.R")  lambda_vec=c(0,0.01,0.03,0.1,0.3,1,3,10,30,50,60,65,70,80,100,130)    error_train <- matrix(0,length(lambda_vec),1)  error_val <- matrix(0,length(lambda_vec),1)    for(i in 1:length(lambda_vec)){    lambda=lambda_vec[i]    train <- trainLinearReg(X,y,lambda)    theta=train$theta    error_train[i] <- linearRegCostFunction(theta,X,y,0)    error_val[i] <- linearRegCostFunction(theta,Xval,yval,0)   }    validationCurve <- list("error_train"=error_train,"error_val"=error_val,"lambda_vec"=lambda_vec)  return(validationCurve)  }