validationCurveLogistic <- function(X,y,Xval,yval){    source("./functions/trainLogisticReg.R")  source("./functions/costFunctionReg.R")  lambda_vec=c(0.001,0.003,0.01,0.03,0.1,0.3,1,3,10)    error_train <- matrix(0,length(lambda_vec),1)  error_val <- matrix(0,length(lambda_vec),1)    for(i in 1:length(lambda_vec)){    lambda=lambda_vec[i]    train <- trainLogisticReg(X,y,lambda)    theta=train$theta    error_train[i] <- costFunctionReg(theta,X,y,0)    error_val[i] <- costFunctionReg(theta,Xval,yval,0)   }    validationCurveLogistic <- list("error_train"=error_train,"error_val"=error_val,"lambda_vec"=lambda_vec)  return(validationCurveLogistic)  }